import React, {useState, useContext} from 'react'
import AppBar from '@material-ui/core/AppBar'
import Toolbar from '@material-ui/core/Toolbar'
import Button from '@material-ui/core/Button'
import { Link } from 'react-router-dom'
import Signup from '../pages/Signup'
import Login from '../pages/Login'
import FarmerHarvestPhotos from "./FarmerHarvestPhotos.js"
import FarmerHarvestList from '../components/FarmerHarvestList'
import {HarvestContext} from '../context/HarvestProvider'


function Navbar(props){

const [toggle, setToggle]= useState(true)
const [ hideform, setHideform ] = useState(false)
const [isClicked, setIsClicked] = useState(false)
const [myHarvestIsClicked, setMyHarvestIsClicked] = useState(false)
const {home, setHome} = useContext(HarvestContext)


const handleSignupLogin =()=> {
    if(toggle === true){

   return( 
    <>
        <Signup  setHideform={setHideform} hideform={hideform}/>
   </>
   )
    }
    if(toggle === false){

    return(
    <>
        <Login   setHideform={setHideform} hideform={hideform}/>
    </>
   )
    }
}
const handleToggleLogin=()=>{

    setToggle(false)
    setHideform(false)

   loginButton.style.visibility = "hidden"



}
const handleToggleSignup=()=>{

   setToggle(true) 
   setHideform(false)
   const signupButton =  document.getElementById("button-signup")
   signupButton.style.visibility = "hidden"

}

return (
    <AppBar >
        <Toolbar>
            <Button id="button-home"
            color="inherit"  
            onClick={()=> {

                setIsClicked(false)
                setMyHarvestIsClicked(false)

            }}
            component={Link} to='/'>
                Home
            </Button>

            <Button 
            id="button-login"
            color="inherit" 
            style={{visibility: "visible"}}
            onClick={handleToggleLogin}
            to='/login'
            >
                Login
            </Button>

            <Button 
            id="button-signup"
            color="inherit" 
            style={{visibility: "visible"}}
            onClick={handleToggleSignup} 
            to='/signup'
            >
                Signup
            </Button>

            < >
                <Button 
                className={!hideform &&'hide-container'} 
                color="inherit" 
                component={Link} 
                to='/farmer-harvest'
                onClick={()=> setIsClicked(true) && setHome(false)}
                >
                    Farmer's Harvest Page
                </Button>
                <Button 
                className={!hideform &&'hide-container'} 
                color="inherit" 
                component={Link} 
                to='/farmer-harvest'
                onClick={()=> setIsClicked(!isClicked)}
                >
                    Image Search
                </Button>
                <Button 
                className={!hideform &&'hide-container'} 
                color="inherit" 
                component={Link} 
                to='/farmer-harvest'
                onClick={()=> setMyHarvestIsClicked(!myHarvestIsClicked)}
                >
                    My Harvests
                </Button>

              {handleSignupLogin()}

            </>
        </Toolbar>
        <h1 className="header-text" >Urban Harvest</h1>
        <h2 className="no-waste">No waste is good waste.</h2>
        <>
        <FarmerHarvestPhotos isClicked={isClicked} />
        <FarmerHarvestList myHarvestIsClicked={myHarvestIsClicked}/>
        </>
    </AppBar>
)


}

export default Navbar
