import React, { useState }from 'react'
import  Unsplash,{ toJson } from "unsplash-js"
//import Access_Key from './.env'






export default function FarmerHarvestPhotos() {



const [query, setQuery] = useState("")
const [ pics, setPics ] = useState([])


const unsplash = new Unsplash({
    accessKey: "7LtZFhlWzGy0OPYYT0GVrfrXXt7yrVIsDzsPTvl93lk" 
    //...other fetch options
     // `fetch` options to be sent with every request
  //headers: { 'X-Custom-Header': 'foo' },
  });


//syntax for searching photos
//search.photos(keyword, page, per_page, filters)

const searchPhotos = async(event) => {
  event.preventDefault()

   unsplash.search
   .photos(query)
   .then((toJson))
   .then((json) => {
       setPics(json.results)
   })
  };
//searchPhotos(unsplash)

let offsetX, offsetY
const move = e =>
{
    const dragMe = e.target
    dragMe.style.left = `${e.pageX-offsetX}px`
    dragMe.style.top = `${e.pageY-offsetY}px`
}
const add = e =>
{
    const dragMe=e.target
    offsetX=e.clientX-dragMe.getBoundingClientRect().left
    offsetY=e.clientY-dragMe.getBoundingClientRect().top
    dragMe.addEventListener('mousemove', move)
}
const remove = e => 
{
    const el=e.target 
    el.removeEventListener('mousemove', remove)

}


return(

<div id="dragMe"className="unsplash-App"onMouseDown={add} onMouseUp={remove} >
     <div className="unsplash-container" >
         <h1 className="unsplash-title">Harvest Image Search</h1>

      <form className="unsplash-form" onSubmit={searchPhotos}> 
        <label className="unsplash-label" htmlFor="query"> 
          {" "}
        </label>
        <input
          type="text"
          name="query"
          className="unsplash-input"
          placeholder={`Try "carrots" or "apples"`}
          value={query}
          onChange={(e) => setQuery(e.target.value)}
        />
        <button type="submit" className="unsplash-button">
          Search
        </button>
      </form>
      <div className="card-list">
          {
          pics.map((pic) => 
            <div className="card">
                <img
                    className="card--image"
                    alt={pic.alt_description}
                    src={pic.urls.full}
                    width="50%"
                    height="50%"
                ></img>
            </div>)
          }
      </div>
      </div>
</div>
  );
}
