import React, { useContext, useEffect, useState, useRef } from 'react'
import Harvest from './Harvest'
import {HarvestContext} from '../context/HarvestProvider'
import {v4} from 'uuid'
import Grid from '@material-ui/core/Grid'
import Panel from './Panel/Panel'



function FarmerHarvestList( props) {

    const { harvests, getAllHarvests, home, setHome} = useContext(HarvestContext)


    const {myHarvestIsClicked, handleDrag} = props



    useEffect(() => {

        getAllHarvests()


    }, []
    )


    return (
      <>
      <Panel></Panel>
       <div className="panel_container" 
                  style={myHarvestIsClicked 
                    ? 
                  {visibility: "visible", position: 'absolute'} 
                    :
                  {visibility: "hidden", position: 'absolute'}}
            >

                 <h1 className="myHarvestText">My Harvests</h1>
                <Grid item xs={12} className="myHarvestList">
                    {harvests.map(harvest => 
                              <Harvest 
                                  title={harvest.title} 
                                  image={harvest.harvestImg} 
                                  label={harvest.body} 
                                  farmer={harvest.userHandle}
                                  key={v4()} 
                              />)
                    }
                </Grid>

            </div>

        </>

    )
}

export default FarmerHarvestList
