import React, { useState, useContext } from 'react'
import  { UserContext } from '../context/UserProvider'
import Grid from '@material-ui/core/Grid'


export default function HarvestPublishing(props) {


    const { user } = useContext(UserContext)
    const { addHarvest } = useContext(userContext)
    const [inputs, setInputs] = useState(user)
    const { hideform, setHideform } = props


    function handleChange(e){
        const { name, value } = e.target
        setInputs(prevInputs => ({
            ...prevInputs, 
            [name]: value
       }))
    }

    function handleSubmit(e){
        e.preventDefault()
         addHarvest(inputs)

    }



    return (
        <Grid className='publishing-container'>
            <form onSubmit={handleSubmit} className={hideform? 'hide-container':"signup-form"}>
				<label for="message">Message</label>
				<input  
                    type="text" 
                    onChange={handleChange} 
                    value="title"
                    className="title" 
                    name="title" 
                    id="title" 
                    rows="5">
                </input>
                <input
                    type="text"
                    value="image"
                    name="image"
                    onChange={handleChange}
                    />
                <button>Publish</button>
            </form>
        </Grid >
    )
}
