import React, { useState } from 'react'
import emailjs from 'emailjs-com'




export default function Contact (props){

const contactNumber = Math.random() * 100000 | 0;
const {farmer, onClick} = props
//when you click on image a contact form will show up that is connected to js email and the farmer will be contacted. 
//idea: have a verbal recording that can be done to attach to the contact email for the user to leave a voice message. 
const [values, setValues] = useState({
    user_name: '',
    user_email: '',
    message: '',
});
const handleChange = (name)=> (e) => {
    setValues({ ...values, [e.target.name]: e.target.value });
};

const isFormValid = () => {
    if (!values.name || !values.email || !values.message) {

  return false;}
  else {
  return true;}

};

const sendEmail = (e)=> {
    emailjs.sendForm('user_oQB8bFJjV5Vpe', 'contact_form')
    .then(function() {
      console.log('SUCCESS!');

    }, function(error) {
      console.log('FAILED...', error);
    });
}


const  handleContact = (e) => {
    e.preventDefault()
    !isFormValid()? console.log("falto algo") : sendEmail(e)

}

    return (

            <>
            <div>
                    <form id="contact_form" onSubmit={handleContact}>
								<input onChange={handleChange}type="hidden" name="contact_number" value={contactNumber}/>
								<label>Name</label>
                                <br/>
								<input onChange={handleChange} type="text" name="user_name"/>
                                <br/>
								<label>Email</label>
                                <br/>
								<input onChange={handleChange} type="email" name="user_email"/>
                                <br/>
								<label>Message</label>
                                <br/>
								<textarea onChange={handleChange} name="message"></textarea>
                                <br/>
								<input type="submit" value="Send"/>
                            <button style={{margin: 0}} onClick={onClick}>X</button> 
                        <p >Contact: {farmer}</p>
					</form>
            </div>
			</>


    )
}
