import React, { useContext, useEffect, useState, useRef } from 'react'
import Harvest from './Harvest'
import {HarvestContext} from '../context/HarvestProvider'
import {v4} from 'uuid'
import Grid from '@material-ui/core/Grid'
import Panel from './componenets/Panel/'



function FarmerHarvestList({handleDrag}, props) {

    const { harvests, getAllHarvests, home, setHome} = useContext(HarvestContext)
    const [ mouseDown, setMouseDown ] = useState(false)

    const {myHarvestIsClicked} = props

    const handleMouseDown = () => setMouseDown(true)


      useEffect(() => {
        const handleMouseUp = () => setMouseDown(false)
        window.addEventListener('mouseup', handleMouseUp)
        return () => {
        window.addEventListener('mouseup', handleMouseUp)
        }
      }, [])

      useEffect(() => {
        const handleMouseMove = (e) => handleDrag(e.movementX, e.movementY)

        if(mouseDown) {
          window.addEventListener('mousemove', handleMouseMove)
        }
        return () => {
          window.removeEventListener('mousemove',  handleMouseMove)
        }
      }, [mouseDown, handleDrag])


    useEffect(() => {

        getAllHarvests()


    }, []
    )


    return (
      <>
      <Panel>
            <Grid className="panel_container" onMouseDown={handleMouseDown} 
                  style={myHarvestIsClicked 
                    ? 
                  {visibility: "visible", position: 'absolute'} 
                    :
                  {visibility: "hidden", position: 'absolute'}}
            >
                 <h1 className="myHarvestText">My Harvests</h1>
                <Grid item xs={12} className="myHarvestList">
                    {harvests.map(harvest => 
                              <Harvest 
                                  title={harvest.title} 
                                  image={harvest.harvestImg} 
                                  label={harvest.body} 
                                  farmer={harvest.userHandle}
                                  key={v4()} 
                              />)
                    }
                </Grid>
            </Grid>
        </Panel>
        </>

    )
}

export default FarmerHarvestList
