import React, { useContext, useEffect, useState, useRef } from 'react'
import Harvest from './Harvest'
import {HarvestContext} from '../context/HarvestProvider'
import {v4} from 'uuid'
import Panel from './Panel/Panel'

import { Direction } from './Panel/constants'
import Resizer from './Panel/Resizer'


function FarmerHarvestList() {

    const { harvests, getAllHarvests, myHarvestIsClicked, setIsClicked} = useContext(HarvestContext)

    const panelRef = useRef(null)


    useEffect(() => {

      getAllHarvests()


  }, []
  )


     const handleResize = (direction, movementX, movementY)=> {
         console.log(direction, movementX, movementY)

         const panel = panelRef.current
         if(!panel) return

         const { width, height, x, y } = panel.getBoundingClientRect()

         const resizeTop = () => {
             panel.style.height = `${height - movementY}px`
             panel.style.top = `${y + movementY}px`

         }
         const resizeRight = () => {
             panel.style.width = `${width - movementX}px`

         }
         const resizeBottom = () => {
             panel.style.height = `${height - movementY}px`

         }
         const resizeLeft = () => {
             panel.style.width = `${width - movementX}px`
             panel.style.left = `${x + movementX}px`

         }
         switch (direction){
             case Direction.TopLeft:
                 resizeTop();
                 resizeLeft();
                 break;

             case Direction.Top:
                 resizeTop();
                 break;

             case Direction.TopRight:
                 resizeTop();
                 resizeRight();
                 break;

             case Direction.Right:
                 resizeRight();
                 break;

             case Direction.BottomRight:
                 resizeBottom();
                 resizeRight();
                 break;

             case Direction.Bottom:
                 resizeBottom();
                 break;

             case Direction.BottomLeft:
                 resizeBottom();
                 resizeLeft();
                 break;

             case Direction.Left:
                 resizeLeft();
                 break;

             default:
                 break;

         }

        }

    return (



       <div 
                  style={myHarvestIsClicked 
                    ? 
                  {visibility: "visible"} 
                    :
                  {visibility: "hidden"}}

       className="farmerHarvest-App">
             <Panel>
              <Resizer onResize={handleResize}/> 

                <h1 className="myHarvestText">My Harvests</h1>
                <div item xs={12} className="myHarvestList">
                    {harvests.map(harvest => 
                              <Harvest 
                                  title={harvest.title} 
                                  image={harvest.harvestImg} 
                                  label={harvest.body} 
                                  farmer={harvest.userHandle}
                                  key={v4()} 
                              />)
                    }
                </div>
                </Panel>     
        </div>



    )
}

export default FarmerHarvestList
