import React, { useRef, useState } from 'react'
import PanelHeader from '../PanelHeader'
import { Direction } from './constants'
import Resizer from './Resizer'




const Panel = ({children}) => {

   const panelRef = useRef(null)
   const [isClicked, setIsClicked]= useState(true)

   const handleDrag = (movementX, movementY) => {
        const panel = panelRef.current

        if(!panel)  return


        const { x, y } = panel.getBoundingClientRect()

        panel.style.left = `${x + movementX}px`
        panel.style.top = `${y + movementY}px`

    }

    const handleResize = (direction, movementX, movementY)=> {
        console.log(direction, movementX, movementY)

        const panel = panelRef.current

        if(!panel) return

        const { width, height, x, y } = panel.getBoundingClientRect()

        const resizeTop = () => {
            panel.style.height = `${height - movementY}px`
            panel.style.top = `${y + movementY}px`

        }
        const resizeRight = () => {
            panel.style.width = `${width - movementX}px`

        }
        const resizeBottom = () => {
            panel.style.height = `${height - movementY}px`

        }
        const resizeLeft = () => {
            panel.style.width = `${width - movementX}px`
            panel.style.left = `${x + movementX}px`

        }
        switch (direction){
            case Direction.TopLeft:
                resizeTop();
                resizeLeft();
                break;

            case Direction.Top:
                resizeTop();
                break;

            case Direction.TopRight:
                resizeTop();
                resizeRight();
                break;

            case Direction.Right:
                resizeRight();
                break;

            case Direction.BottomRight:
                resizeBottom();
                resizeRight();
                break;

            case Direction.Bottom:
                resizeBottom();
                break;

            case Direction.BottomLeft:
                resizeBottom();
                resizeLeft();
                break;

            case Direction.Left:
                resizeLeft();
                break;

            default:
                break;

        }
    }


    return(

        <div className="panel" isClicked={isClicked} ref={panelRef}>
            <div className="panel_container" 
                  style={isClicked 
                    ? 
                  {visibility: "visible", position: 'absolute'} 
                    :
                  {visibility: "hidden", position: 'absolute'}}
            >
                <Resizer onResize={handleResize}/> 
                <PanelHeader  handleDrag={handleDrag}/>
                <div className="panel_content">
                {children}
                </div>
            </div>
        </div>

    )
}

export default Panel
