import React, { useEffect, useState } from 'react';
import PropTypes from 'prop-types';
import FarmerHarvestPhotos from './FarmerHarvestPhotos';

export default function DragMove(props) {


  const [isDragging, setIsDragging] = useState(false);
  const { func, element, shape, bool, string } = PropTypes;
  const {
    onPointerDown,
    onPointerUp,
    onPointerMove,
    onDragMove,
    children,
    style,
    className,
  } = props;




  const handleDragMove = e => {
        //extract movementX and movementY 
        //from the event and add them to the 
        //current translateX and translateY
        console.log("eTarget", e.target)
        // translateX(target + movementX)
  }

  const handlePointerDown = (e) => {
    setIsDragging(true);

    onPointerDown(e);
  };

  const handlePointerUp = (e) => {
    setIsDragging(false);

    onPointerUp(e);
  };

  const handlePointerMove = (e) => {
    if (isDragging) onDragMove(e);

    onPointerMove(e);
  };

  useEffect(() => {
    window.addEventListener('pointerup', handlePointerUp);

    return () => {
      window.removeEventListener('pointerup', handlePointerUp);
    }
  }, []);

  DragMove.propTypes = {
    onDragMove: handleDragMove.isRequired,
    onPointerDown: onPointerDown,
    onPointerUp: onPointerUp,
    onPointerMove: onPointerMove,
    children: <div />,
    style: shape({}),
    className: string,
  }




  return (
    <div
      onPointerDown={handlePointerDown}
      onPointerMove={handlePointerMove}
      className={className}
      style={style}
    >
      <FarmerHarvestPhotos />
      {children}
    </div>
  );



}
