import React, { useEffect, useState } from 'react';
import PropTypes from 'prop-types';
import  Unsplash,{ toJson } from "unsplash-js"

//import Access_Key from './.env'






export default function FarmerHarvestPhotos(props) {


    const [isDragging, setIsDragging] = useState(false);
const [query, setQuery] = useState("")
const [ pics, setPics ] = useState([])

const {
    onPointerDown,
    onPointerUp,
    onPointerMove,
    onDragMove,
    children,
    style,
    className,
    isSvg = false,
  } = props;

const unsplash = new Unsplash({
    accessKey: "7LtZFhlWzGy0OPYYT0GVrfrXXt7yrVIsDzsPTvl93lk" 
    //...other fetch options
     // `fetch` options to be sent with every request
  //headers: { 'X-Custom-Header': 'foo' },
  });


//syntax for searching photos
//search.photos(keyword, page, per_page, filters)

const searchPhotos = async(event) => {
  event.preventDefault()

   unsplash.search
   .photos(query)
   .then((toJson))
   .then((json) => {
       setPics(json.results)
   })
  };
//searchPhotos(unsplash)

const handlePointerDown = (e) => {
    setIsDragging(true);

    onPointerDown(e);
  };

  const handlePointerUp = (e) => {
    setIsDragging(false);

    onPointerUp(e);
  };

  const handlePointerMove = (e) => {
    if (isDragging) onDragMove(e);

    onPointerMove(e);
  };

  useEffect(() => {
    window.addEventListener('pointerup', handlePointerUp);

    return () => {
      window.removeEventListener('pointerup', handlePointerUp);
    }
  }, []);

  // Dynamically render a <g> or <div> tag
  const Tag = isSvg ? 'g' : 'div';

return(
<>
<div className="unsplash-App" onMouseDown={add} onMouseUp={remove} >
     <div className="unsplash-container" >
         <h1 className="unsplash-title">Harvest Image Search</h1>

      <form className="unsplash-form" onSubmit={searchPhotos}> 
        <label className="unsplash-label" htmlFor="query"> 
          {" "}
        </label>
        <input
          type="text"
          name="query"
          className="unsplash-input"
          placeholder={`Try "carrots" or "apples"`}
          value={query}
          onChange={(e) => setQuery(e.target.value)}
        />
        <button type="submit" className="unsplash-button">
          Search
        </button>
      </form>
      <div className="card-list">
          {
          pics.map((pic) => 
            <div className="card">
                <img
                    className="card--image"
                    alt={pic.alt_description}
                    src={pic.urls.full}
                    width="50%"
                    height="50%"
                ></img>
            </div>)
          }
      </div>
      </div>
</div>
</>
  );
}
