import React from 'react'
import Grid from '@material-ui/core/Grid'
import FarmerHarvestPhotos from "./FarmerHarvestPhotos"
import FarmerHarvestList from "./FarmerHarvestList"
import HarvestPublishing from "./HarvestPublishing"


export default function FarmersHarvestPage() {
    const unsplashApp = document.getElementById("drag-me")
    let mousePositionTop
    let mousePositionLeft
   const [isDragging, setIsDragging] = useState



    const handleStartMove=()=>{
        console.log("down")
        isDragging = true

    }
    const handleStopMove=('mouseup', (e)=> {
        console.log("up")
        mousePositionLeft = e.x
        mousePositionTop = e.y
        isDragging = false
        }
    )

    return(
    <>

    <Grid  className='farmer-container' container >
    <div style={{width: '100%', height: '3vh'}}>
    </div>
       <HarvestPublishing/>
       <div id="drag-me"className="unsplash-App" 
            onMouseMove={(e, style)=>{
                console.log("it moved")
                isDragging
                ? 
                (style={left: e.x - mousePositionLeft + "px"})
                :
                (style={top: e.y - mousePositionTop + "px"})
            }
        }
            onMouseDown={handleStartMove}
            onMouseUp={handleStopMove}
           >
            <div className="unsplash-container">
                <h1 className="unsplash-title">Harvest Image Search</h1>
                <FarmerHarvestPhotos/>
            </div>
       </div>
       <FarmerHarvestList/>
    </Grid>
    </>

    )
}
